import logo from './logo.svg';
import './App.css';
import NavBar from './components/navbar';
import Counters from './components/counters';

function App() {

  state = { 

    counters: [
        {id: 1, value: 0},
        {id: 2, value: 0},
        {id: 3, value: 0},
        {id: 4, value: 0},
    ]

 } 

 handleIncrement = (counterId) => {
    console.log(" Increment ", counterId)

    const counters = [...this.state.counters]

    counters.forEach(element => {

        if (counterId === element.id){
            counters[counterId-1].value += 1;
            console.log("Increment Done", counters)
        }

    });

    this.setState(
        {
            counters
        }
    )

 }

 handleDecrement = (counterId) => {
    console.log(" Decrement ", counterId)

    const counters = [ ...this.state.counters ]

    counters.forEach(element => {

        if( element.id === counterId ){
            counters[element.id - 1].value -= 1
            console.log("Decrement done")
        }

    })

    this.setState({
        counters
    })


 }

 handleReset = (counterId) => {
    console.log("Reset Initiated", counterId)

    // const counters = this.state.counters.map(
    //     c => {

    //         if(c.id === counterId){
    //             console.log("Found the reset ID", c.value)
    //             c.value += 1
    //         }
    //     }
    // )

    const counters = [ ...this.state.counters ]

    counters.forEach(element => {

        if( element.id === counterId ){

            element.value = 0

        }

    })

    this.setState({
        counters
    })

    console.log(counters)

 }

 handleDelete = (counterId) => {
    console.log("Delete called", counterId)
    const counters = this.state.counters.filter(
        c => c.id != counterId
    )
    this.setState(
        {
            counters
        }
    )
 } 


  return (
    <>
      <NavBar />
      <main className='container'>
        <Counters 
        onReset={this.handleReset}
        onIncrement={this.handleIncrement}
        onDelete={this.handleDelete}
        onDecrement={this.handleDecrement}/>
      </main>
    </>
  );
}

export default App;
