import React, { Component } from 'react';
import Starter from './starter';
import Counter from './counter';

class Counters extends Component {
    state = { 

        counters: [
            {id: 1, value: 0},
            {id: 2, value: 0},
            {id: 3, value: 0},
            {id: 4, value: 0},
        ]

     } 

     handleDelete = (counterId) => {
        console.log("Delete called", counterId)
     } 

    render() { 
        return (
            <div>
                {/* <Counter></Counter>
                <Counter></Counter>
                <Counter></Counter>
                <Counter></Counter> */}

                {this.state.counters.map(
                    counter => <Counter key={counter.id} value={counter.value} onDelete={this.handleDelete} selected = {true}>

                    <h4>ID: {counter.id}</h4>

                    </Counter>
                )}

            </div>
        );
    }
}

export default Counters;
