import React, { Component } from 'react';


class Counter extends Component {
    state = { 
        count: 0
    }

    handleCounter = () => {

        this.setState({
            count: this.state.count + 1
        })

    }

    render() { 
        return (
            <div>
                <p className="badge badge-primary">this.state.count</p>
                <button onClick={this.handleCounter} className="btn btn-secondary">Increment</button>
            </div>
        );
    }
}

export default Counter;
