import React, { Component } from 'react';
import Starter from './starter';
import Counter from './counter';

class Counters extends Component {
    state = { 

        counters: [
            {id: 1, value: 0},
            {id: 2, value: 0},
            {id: 3, value: 0},
            {id: 4, value: 0},
        ]

     } 

     handleIncrement = (counterId) => {
        console.log(" Increment ", counterId)


        const counters = [...this.state.counters]
        let counter = { ...counters[counterId] }
        console.log(counter)
        const countersIndex = counters.indexOf(counterId)
        counters[countersIndex].value += 1;
        console.log("Increment Done", counters)



     }

     handleDecrement = (counterId) => {
        console.log(" Decrement ", counterId)
     }

     handleReset = (counterId) => {
        console.log("Reset Initiated", counterId)

        const counters = this.state.counters.map(
            c => {

                if( this.state.counters.findIndex(c)){
                    console.log("Found the reset ID")
                }

                // this.state.counters.forEach(element => {

                //     if ( element.id === c.id ){
                //         console.log("The reset id is found", element.id)
                //     }

                // });

                // for (let i = 0; i < this.state.counters.length; i++){

                //     if ( c.id === this.counters[i].id){
                //         console.log("The reset is found")
                //     }

                // }
            }
        )

     }

     handleDelete = (counterId) => {
        console.log("Delete called", counterId)
        const counters = this.state.counters.filter(
            c => c.id != counterId
        )
        this.setState(
            {
                counters
            }
        )
     } 

    render() { 

        if (this.state.counters.length === 0) {

            return (
                <h3>There are nothing here, <b>Dumb Fucker</b></h3>
            )

        }

        return (
            <div>
                {/* <Counter></Counter>
                <Counter></Counter>
                <Counter></Counter>
                <Counter></Counter> */}

                {this.state.counters.map(
                    counter => 
                    <Counter 
                    key={counter.id}
                    value={counter.value}
                    id = {counter.id}
                    onDelete={this.handleDelete}
                    onReset={this.handleReset}
                    onIncrement={this.handleIncrement}
                    onDecrement={this.handleDecrement}
                    >

                    <h4>ID: {counter.id}</h4>

                    </Counter>
                )}

            </div>
        );
    }
}

export default Counters;
